$(document).ready(function () {const cards = $('.card');const carouselImage = $('#carouselImage');const cardCount = cards.length;let currentIndex = 0;let interval;function showImage(index) {const imageUrl = cards.eq(index).data('image');carouselImage.fadeOut(250, function () {$(this).attr('src', imageUrl).fadeIn(500);});}function startTimer(index) {const progressBar = cards.eq(index).find('.progress-bar');let progress = 0;progressBar.width(0).css('opacity', 1);interval = setInterval(function () {progress += 100/100; progressBar.width(`${progress}%`);if (progress >= 100) {clearInterval(interval);progressBar.css('opacity', 0);currentIndex = (index + 1) % cardCount;showImage(currentIndex);setCurrentCard(currentIndex);startTimer(currentIndex);setTimeout(function () {progressBar.width(0);}, 500);}}, 40);}function setCurrentCard(index) {cards.removeClass('active');cards.eq(index).addClass('active');}cards.on('click', function () {const clickedIndex = $(this).index();clearInterval(interval);cards.eq(currentIndex).find('.progress-bar').width(0).css('opacity', 0);showImage(clickedIndex);setCurrentCard(clickedIndex);currentIndex = clickedIndex;startTimer(clickedIndex);});showImage(0);setCurrentCard(0);startTimer(0);});
